const container = document.getElementById("products-container");
const searchBox = document.getElementById("searchBox");
const categoryFilters = document.getElementById("categoryFilters");

let activeMainCategory = "ุงููู";

function parseCategories() {
  return productsData.map(p => {
    const [main, sub, subsub] = p.category.split(">");
    return { ...p, main, sub, subsub };
  });
}

function getMainCategories() {
  const parsed = parseCategories();
  return ["ุงููู", ...new Set(parsed.map(p => p.main))];
}

function renderCategoryFilters() {
  categoryFilters.innerHTML = "";
  getMainCategories().forEach(cat => {
    const btn = document.createElement("button");
    btn.className = `category-button ${activeMainCategory === cat ? "active" : ""}`;
    btn.innerText = cat;
    btn.addEventListener("click", () => {
      activeMainCategory = cat;
      renderProducts(searchBox.value);
      renderCategoryFilters();
    });
    categoryFilters.appendChild(btn);
  });
}

function renderProducts(filter = "") {
  container.innerHTML = "";
  const parsed = parseCategories().filter(p =>
    (activeMainCategory === "ุงููู" || p.main === activeMainCategory) &&
    p.name.includes(filter)
  );

  // Group by main category > sub category > sub-sub category
  const grouped = {};
  parsed.forEach(p => {
    if (!grouped[p.main]) grouped[p.main] = {};
    if (!grouped[p.main][p.sub]) grouped[p.main][p.sub] = {};
    if (!grouped[p.main][p.sub][p.subsub]) grouped[p.main][p.sub][p.subsub] = [];
    grouped[p.main][p.sub][p.subsub].push(p);
  });

  Object.keys(grouped).forEach(mainCat => {
    const mainSection = document.createElement("div");
    mainSection.className = "product-category";
    mainSection.innerHTML = `<h2>${mainCat}</h2>`;

    Object.keys(grouped[mainCat]).forEach(subCat => {
      const subTitle = document.createElement("h3");
      subTitle.className = "sub-category-title";
      subTitle.textContent = subCat;
      mainSection.appendChild(subTitle);

      Object.keys(grouped[mainCat][subCat]).forEach(subsub => {
        const subsubTitle = document.createElement("h4");
        subsubTitle.className = "sub-sub-category-title";
        subsubTitle.textContent = subsub;
        mainSection.appendChild(subsubTitle);

        const grid = document.createElement("div");
        grid.className = "product-grid";

        grouped[mainCat][subCat][subsub].forEach(prod => {
          const card = document.createElement("div");
          card.className = "product-card";
          card.innerHTML = `
              <img src="${prod.image}" loading="lazy" alt="${prod.name}">
              <div class="product-info"><h3>${prod.name}</h3></div>
            `;
          grid.appendChild(card);
        });

        mainSection.appendChild(grid);
      });
    });

    container.appendChild(mainSection);
  });
}

renderCategoryFilters();
renderProducts();

searchBox.addEventListener("input", e => renderProducts(e.target.value));
